import { Box, Container ,Button , Text,Th,Thead,Tbody, Tr,Table,TableContainer} from "@chakra-ui/react";
import { useContext, useEffect, useState } from 'react';
import { GlobalContext } from './context/GlobalWrapper'
import Row from './components/Row';
import {FormControl,Input} from '@chakra-ui/react'
import {AiOutlineSearch } from "react-icons/ai";
import {AiOutlinePlus } from "react-icons/ai";
function App() {
  const { FetchUsers, Search, users ,  onOpen,    isOpen, onClose, } =  useContext(GlobalContext);


  const [query, setQuery] = useState('');
  useEffect(() => {
    FetchUsers();
  }, []);
  const SearchHandler = () => {
    Search(query);
  };
  const onchangeHandler = (e) => {
    setQuery(e.target.value);
  };
  return (
    <div className="App">
  <Container max={'full'} p="4" fontSize={"18"}>

        <Box rounded="lg" boxShadow="base" p="4" >
             <Box mt="2" gap="2" mb="4" display={'flex'}>
                 <FormControl>
                 <Input type='text' onChange={onchangeHandler}/>
                 </FormControl>
                 <Button leftIcon={<AiOutlineSearch />} colorScheme='teal' variant='outline' maxW={'300px'} minW="150px" onClick={()=>SearchHandler()}>Search</Button>
              </Box>
         </Box>
         <Box boxShadow="base" mt="5"rounded="lg">
             <Box  justifyContent={"space-between"}display={'flex'} p="4" >
             <Text fontSize="xl" fontWeight="bold">
              List Users
            </Text>
            <Button
              colorScheme="teal"
              variant="outline"
              maxW={'300px'}
              minW="150px"
              leftIcon={<AiOutlinePlus fontSize={'20px'} />} onClick={onOpen}>
              Add User
            </Button>
             </Box> 
             <TableContainer>
            <Table variant='simple'>
            <Thead>
                <Tr>
                  <Th>Avatar</Th>
                  <Th>Fullname</Th>
                  <Th>Email</Th>
                  <Th>Age</Th>
                  <Th>Country</Th>
                  <Th>Actions</Th>
                </Tr>
              </Thead>
              <Tbody>
                {users?.map(({ _id, fullname, email, age, country }) => {
                  return (
                    <Row
                      id={_id}
                      fullname={fullname}
                      email={email}
                      age={age}
                      country={country}
                    />
                  );
                })}
   </Tbody>
             </Table>
</TableContainer> 
             </Box>

  </Container>
    </div>
  );
}

export default App;
