import { Box, Button, Container } from "@chakra-ui/react";
import {
  FormControl,
  FormLabel,
  FormErrorMessage,
  FormHelperText,
} from '@chakra-ui/react'

function App() {
  return (
    <div className="App">
     <Container max={'full'} p="4" fontSize={"18"}>

<Box rounded="lg" boxShadow="base" p="4" >
      <Box mt="2" gap="2" mb="4">
      <FormControl>
  <FormLabel>Email address</FormLabel>
  <Input type='email' />
  <FormHelperText>We'll never share your email.</FormHelperText>
</FormControl>
      </Box>
</Box>
     </Container>
    </div>
  );
}

export default App;
